package chap02_variables;

public class _05_ShortIntLong {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 1. short 타입의 변수 선언 및 초기화
		short sNum1 = 10;
		short sNum2 = 20;
		
		// 2. int 타입의 변수 선언 및 초기화
		int iNum1 = 200;
		int iNum2 = 200000000;
		
		// 3. long 타입의 변수 선언 및 초기화
		// L이나 l을 붙이는 것에 차이는 없다.
		// 습관적으로 long 타입의 변수에 값을 저장할 때는 값에 l, L을 붙여주는 것이 좋다.
		long lNum1 = 100L;
		long lNum2 = 100l;
		long lNum3 = 100;
		
		// int 범위를 초과하는 값을 long에 저장할 때 l, L을 붙이지 않으면 에러가 발생한다.
		long lNum4 = 5000000000000L;
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
